import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
// import '../css/form/ComplaintForm.css';

function ComplaintForm({ userType }) {
    const [form, setForm] = useState({
        identifier: '',
        title: '',
        issue: '',
        location: '',
        nature: '',
        priority: '',
        image: null
    });
    const navigate = useNavigate();

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleImageChange = (e) => {
        setForm({ ...form, image: e.target.files[0] });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        // Construct form data to include the image file if it was uploaded
        const formData = new FormData();
        for (const key in form) {
            formData.append(key, form[key]);
        }

        // Submit form logic here, replace URL with your server endpoint
        // You might want to add form validation before submitting
        try {
            const response = await fetch('/api/complaints', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                // After submission, navigate to a confirmation page or dashboard
                navigate('/dashboard');
            } else {
                console.error('Failed to submit form', await response.text());
            }
        } catch (error) {
            console.error('Error submitting form', error);
        }
    };

    return (
        <div className="complaint-form-container">
            <h2>{userType === 'student' ? 'Student' : 'Staff'} Complaint Form</h2>
            <form onSubmit={handleSubmit} encType="multipart/form-data">
                {/* ...other input fields... */}

                <div>
                    <label htmlFor="imageUpload">
                        Upload an image (optional):
                        <input
                            type="file"
                            name="image"
                            id="imageUpload"
                            onChange={handleImageChange}
                            accept="image/*"
                        />
                    </label>
                </div>

                <button type="submit">Submit Complaint</button>
            </form>
        </div>
    );
}

export default ComplaintForm;
