import React, { useState } from 'react';
import ComplaintForm from '../ComplaintForm';
import ComplaintsTable from '../ComplaintsTable';
import css from './DepartmentDashboard.css'
// import '../css/Dashboard.css'; // Import your CSS file for styling
// import '../css/DepartmentDashboard.css'

const DepartmentDashboard = () => {
    const [showForm, setShowForm] = useState(false);
    const [complaints, setComplaints] = useState([]);

    const handleAddComplaintClick = () => {
        setShowForm(true);
    };

    const handleFormSubmit = (complaint) => {
        setComplaints([...complaints, complaint]);
        setShowForm(false);
    };
    const toggleFormVisibility = () => {
        setShowForm(!showForm);
    };

    return (
        <div className={`dashboard-container ${showForm ? 'blur' : ''}`}>
            <button onClick={toggleFormVisibility}>
                <span className="plus-icon">+</span> Add New Complaint
            </button>
            {showForm && (
                <ComplaintForm onSubmit={handleFormSubmit} />
            )}
            {/* ... rest of your component */}
        </div>
    );
}

export default DepartmentDashboard;
