import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ComplaintsTable from './ComplaintsTableAdmin';
import DashHeader from '../DashHeader';
// import '../css/AdminDashboard.css';

const AdminDashboard = () => {
    const [complaints, setComplaints] = useState([]);

    useEffect(() => {
        fetchComplaints();
    }, []);

    const fetchComplaints = async () => {
        try {
            const res = await axios.get('http://localhost:5000/api/complaints');
            setComplaints(res.data);
        } catch (error) {
            console.error('There was an error fetching the complaints!', error);
        }
    };

    const updateComplaint = async (index, updatedData) => {
        try {
            const updatedComplaint = { ...complaints[index], ...updatedData };
            const res = await axios.patch(`http://localhost:5000/api/complaints/${complaints[index]._id}`, updatedComplaint);
            const updatedComplaints = complaints.map((complaint, i) => (i === index ? res.data : complaint));
            setComplaints(updatedComplaints);
        } catch (error) {
            console.error('There was an error updating the complaint!', error);
        }
    };

    return (
        <div>
            <DashHeader />
            <div className="dashboard-container">
                <h1>Admin Dashboard</h1>
                <ComplaintsTable complaints={complaints} updateComplaint={updateComplaint} />
            </div>
        </div>
    );
};

export default AdminDashboard;
