import React from 'react';
import { Page, Text, View, Document, StyleSheet } from '@react-pdf/renderer';

const styles = StyleSheet.create({
    page: {
        padding: 30,
    },
    section: {
        marginBottom: 10,
    },
    title: {
        fontSize: 24,
        marginBottom: 10,
    },
    label: {
        fontSize: 12,
        fontWeight: 'bold',
    },
    value: {
        fontSize: 12,
    },
});

const ComplaintPDF = ({ complaint }) => (
    <Document>
        <Page size="A4" style={styles.page}>
            <View style={styles.section}>
                <Text style={styles.title}>Complaint Details</Text>
            </View>
            <View style={styles.section}>
                <Text style={styles.label}>Title:</Text>
                <Text style={styles.value}>{complaint.title}</Text>
            </View>
            <View style={styles.section}>
                <Text style={styles.label}>Issue:</Text>
                <Text style={styles.value}>{complaint.issue}</Text>
            </View>
            <View style={styles.section}>
                <Text style={styles.label}>Location:</Text>
                <Text style={styles.value}>{complaint.location}</Text>
            </View>
            <View style={styles.section}>
                <Text style={styles.label}>Assigned Worker:</Text>
                <Text style={styles.value}>{complaint.assignedWorker}</Text>
            </View>
            <View style={styles.section}>
                <Text style={styles.label}>Status:</Text>
                <Text style={styles.value}>{complaint.status}</Text>
            </View>
        </Page>
    </Document>
);

export default ComplaintPDF;
