import React, { useState } from 'react';
import '../css/ComplaintsTable.css';

function ComplaintsTable({ complaints }) {
    const [editIndex, setEditIndex] = useState(-1);
    const [assignedWorker, setAssignedWorker] = useState('');
    const [status, setStatus] = useState('');

    const handleEditClick = (index, currentWorker, currentStatus) => {
        setEditIndex(index);
        setAssignedWorker(currentWorker || '');
        setStatus(currentStatus || '');
    };

    const handleSaveClick = (index) => {
        const updatedComplaints = complaints.map((complaint, i) =>
            i === index ? { ...complaint, assignedWorker, status } : complaint
        );
        setComplaints(updatedComplaints);
        setEditIndex(-1);
    };

    return (
        <table className="complaints-table">
            <thead>
                <tr>
                    <th>Identifier</th>
                    <th>Title</th>
                    <th>Issue</th>
                    <th>Assigned Worker</th>
                    <th>Status</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                {complaints.map((complaint, index) => (
                    <tr key={index}>
                        <td>{complaint.identifier}</td>
                        <td>{complaint.title}</td>
                        <td>{complaint.issue}</td>
                        <td>
                            {editIndex === index ? (
                                <input
                                    type="text"
                                    value={assignedWorker}
                                    onChange={(e) => setAssignedWorker(e.target.value)}
                                />
                            ) : (
                                complaint.assignedWorker
                            )}
                        </td>
                        <td>
                            {editIndex === index ? (
                                <select
                                    value={status}
                                    onChange={(e) => setStatus(e.target.value)}
                                >
                                    <option value="Pending">Pending</option>
                                    <option value="In Progress">In Progress</option>
                                    <option value="Resolved">Resolved</option>
                                </select>
                            ) : (
                                complaint.status
                            )}
                        </td>
                        <td>
                            {editIndex === index ? (
                                <button onClick={() => handleSaveClick(index)}>Save</button>
                            ) : (
                                <button onClick={() => handleEditClick(index, complaint.assignedWorker, complaint.status)}>Edit</button>
                            )}
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
}

export default ComplaintsTable;
