import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { Document, Page, Text, View, StyleSheet, Image, BlobProvider } from '@react-pdf/renderer';
import '../css/ComplaintsForm.css';

// Styles for the PDF document
const styles = StyleSheet.create({
    page: {
        flexDirection: 'column',
        backgroundColor: '#fff',
        padding: 30,
        fontFamily: 'Helvetica',
        fontSize: 12,
    },
    logo: {
        width: 100,
        marginBottom: 20,
    },
    title: {
        fontSize: 18,
        textAlign: 'center',
        marginBottom: 20,
    },
    content: {
        marginBottom: 10,
    },
    footer: {
        position: 'absolute',
        bottom: 30,
        left: 30,
        right: 30,
        fontSize: 12,
        textAlign: 'center',
        color: 'grey',
    },
    seal: {
        width: 100,
        position: 'absolute',
        bottom: 30,
        left: 30,
    },
    signature: {
        width: 100,
        position: 'absolute',
        bottom: 30,
        right: 30,
    },
});

function ComplaintForm() {
    const { userType } = useParams();
    const [complaintData, setComplaintData] = useState({
        // ... (initial form data)
    });
    const [showPreview, setShowPreview] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setComplaintData({ ...complaintData, [name]: value });
    };

    const handleImageChange = (e) => {
        setComplaintData({ ...complaintData, image: e.target.files[0] });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setShowPreview(true);
    };

    const handleEdit = () => {
        setShowPreview(false);
    };

    const handleFinalSubmit = () => {
        // Logic to submit form data to the server
        console.log('Final submission:', complaintData);
        // Redirect or show a success message
    };

    const ComplaintDocument = (
        <Document>
            <Page size="A4" style={styles.page}>
                {/* ... (PDF content) */}
            </Page>
        </Document>
    );

    return (
        <div className="complaint-form-container" onClick={(e) => e.stopPropagation()}>
            {!showPreview ? (
                <form onSubmit={handleSubmit} encType="multipart/form-data">
                    <div className="input-group">
                        <label htmlFor="identifier">Identifier</label>
                        <input
                            type="text"
                            id="identifier"
                            name="identifier"
                            value={complaintData.identifier}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="title">Title</label>
                        <input
                            type="text"
                            id="title"
                            name="title"
                            value={complaintData.title}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="issue">Issue</label>
                        <textarea
                            id="issue"
                            name="issue"
                            value={complaintData.issue}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="location">Location</label>
                        <input
                            type="text"
                            id="location"
                            name="location"
                            value={complaintData.location}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="nature">Nature of the Complaint</label>
                        <input
                            type="text"
                            id="nature"
                            name="nature"
                            value={complaintData.nature}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="phone">Phone Number</label>
                        <input
                            type="tel"
                            id="phone"
                            name="phone"
                            value={complaintData.phone}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="priority">Priority</label>
                        <select
                            id="priority"
                            name="priority"
                            value={complaintData.priority}
                            onChange={handleChange}
                            required
                        >
                            <option value="low">Low</option>
                            <option value="medium">Medium</option>
                            <option value="high">High</option>
                        </select>
                    </div>
                    <div className="input-group">
                        <label htmlFor="image">Image (Optional)</label>
                        <input
                            type="file"
                            id="image"
                            name="image"
                            onChange={handleImageChange}
                            accept="image/*"
                        />
                    </div>
                    <button type="submit">Submit Complaint</button>
                </form>
            ) : (
                <BlobProvider document={ComplaintDocument}>
                    {({ blob, url, loading, error }) => {
                        if (loading) {
                            return <div>Loading preview...</div>;
                        }
                        return (
                            <div>
                                <iframe src={url} style={{ width: '100%', height: '500px' }} title="Preview" />
                                <div>
                                    <button onClick={handleEdit}>Edit</button>
                                    <button onClick={handleFinalSubmit}>Confirm and Submit</button>
                                    <a href={url} download="ComplaintDetails.pdf">
                                        <button>Download PDF</button>
                                    </a>
                                </div>
                            </div>
                        );
                    }}
                </BlobProvider>
            )}
        </div>
    );
}

export default ComplaintForm;
