import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { Document, Page, Text, View, StyleSheet, Image, BlobProvider } from '@react-pdf/renderer';
import '../css/ComplaintsForm.css';

// Styles for the PDF document
const styles = StyleSheet.create({
    page: {
        flexDirection: 'column',
        backgroundColor: '#fff',
        padding: 30,
        fontFamily: 'Helvetica',
        fontSize: 12,
    },
    logo: {
        width: 100,
        marginBottom: 20,
    },
    title: {
        fontSize: 18,
        textAlign: 'center',
        marginBottom: 20,
    },
    content: {
        marginBottom: 10,
    },
    footer: {
        position: 'absolute',
        bottom: 30,
        left: 30,
        right: 30,
        fontSize: 12,
        textAlign: 'center',
        color: 'grey',
    },
    seal: {
        width: 100,
        position: 'absolute',
        bottom: 30,
        left: 30,
    },
    signature: {
        width: 100,
        position: 'absolute',
        bottom: 30,
        right: 30,
    },
});

const ComplaintForm = () => {
    const { userType } = useParams();
    const [complaintData, setComplaintData] = useState({
        identifier: '',
        title: '',
        issue: '',
        location: '',
        nature: '',
        phone: '',
        priority: '',
        image: null,
    });
    const [showPreview, setShowPreview] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setComplaintData({ ...complaintData, [name]: value });
    };

    const handleImageChange = (e) => {
        setComplaintData({ ...complaintData, image: e.target.files[0] });
    };

    const handlePreview = (e) => {
        e.preventDefault();
        setShowPreview(true);
    };

    const handleEdit = () => {
        setShowPreview(false);
    };

    const handleFinalSubmit = () => {
        // Submit the form data to the server
        console.log('Final submission:', complaintData);
        // Redirect or show a success message
    };

    const ComplaintDocument = (
        <Document>
            <Page size="A4" style={styles.page}>
                <Image
                    style={styles.logo}
                    src="path_to_your_logo.png" // Replace with the path to your logo image
                />
                <Text style={styles.title}>{`Complaint Form - ${userType}`}</Text>
                <Text style={styles.content}>Identifier: {complaintData.identifier}</Text>
                <Text style={styles.content}>Title: {complaintData.title}</Text>
                <Text style={styles.content}>Issue: {complaintData.issue}</Text>
                <Text style={styles.content}>Location: {complaintData.location}</Text>
                <Text style={styles.content}>Nature: {complaintData.nature}</Text>
                <Text style={styles.content}>Phone: {complaintData.phone}</Text>
                <Text style={styles.content}>Priority: {complaintData.priority}</Text>
                {complaintData.image && (
                    <Text style={styles.content}>Image Attached: {complaintData.image.name}</Text>
                )}
                <Image
                    style={styles.seal}
                    src="path_to_your_seal.png" // Replace with the path to your department seal image
                />
                <Image
                    style={styles.signature}
                    src="path_to_signature_placeholder.png" // Replace with the path to your signature placeholder image
                />
                <Text style={styles.footer}>Department Seal</Text>
                <Text style={styles.footer}>Estate Office Sign</Text>
            </Page>
        </Document>
    );

    return (
        <div className="complaint-form-container">
            {!showPreview ? (
                <form onSubmit={handlePreview} encType="multipart/form-data">
                    {/* Your input fields here */}
                    <button type="submit">Preview</button>
                </form>
            ) : (
                <BlobProvider document={ComplaintDocument}>
                    {({ blob, url, loading, error }) => {
                        if (loading) {
                            return <div>Loading preview...</div>;
                        }
                        return (
                            <div>
                                <iframe src={url} style={{ width: '100%', height: '500px' }} title="Preview" />
                                <div>
                                    <button onClick={handleEdit}>Edit</button>
                                    <button onClick={handleFinalSubmit}>Confirm and Submit</button>
                                    <a href={url} download="ComplaintDetails.pdf">
                                        <button>Download PDF</button>
                                    </a>
                                </div>
                            </div>
                        );
                    }}
                </BlobProvider>
            )}
        </div>
    );
};

export default ComplaintForm;
