import React, { useState } from 'react';
// import { useParams } from 'react-router-dom';
import { Document, Page, Text, StyleSheet, Image, BlobProvider } from '@react-pdf/renderer';
import '../css/ComplaintsForm.css';

import logo from '../img/home/image1.jpg';
import seal from '../img/home/image1.jpg';
import signaturePlaceholder from '../img/home/image1.jpg';


// Styles for the PDF document
const styles = StyleSheet.create({
    page: {
        flexDirection: 'column',
        backgroundColor: '#fff',
        padding: 30,
        fontFamily: 'Helvetica',
    },
    logo: {
        width: 60,
        height: 60,
    },
    header: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: 20,
    },
    title: {
        fontSize: 18,
        textAlign: 'center',
        margin: 10,
    },
    fieldContainer: {
        marginBottom: 4,
    },
    label: {
        fontSize: 12,
        fontWeight: 'bold',
    },
    fieldValue: {
        fontSize: 12,
        marginBottom: 10,
    },
    footer: {
        position: 'absolute',
        bottom: 30,
        left: 30,
        right: 30,
        fontSize: 12,
        textAlign: 'center',
        color: 'grey',
    },
    seal: {
        width: 60,
        height: 60,
    },
    signature: {
        width: 60,
        height: 60,
    },
    signaturesContainer: {
        position: 'absolute',
        bottom: 30,
        left: 30,
        right: 30,
        flexDirection: 'row',
        justifyContent: 'space-between',
    },
});

function ComplaintForm() {
    // const { userType } = useParams();
    const [complaintData, setComplaintData] = useState({
        // ... (initial form data)
    });
    const [showPreview, setShowPreview] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setComplaintData({ ...complaintData, [name]: value });
    };

    const handleImageChange = (e) => {
        setComplaintData({ ...complaintData, image: e.target.files[0] });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setShowPreview(true);
    };

    const handleEdit = () => {
        setShowPreview(false);
    };

    const handleFinalSubmit = () => {
        // Logic to submit form data to the server
        console.log('Final submission:', complaintData);
        // Redirect or show a success message
    };

    const ComplaintDocument = (
        <Document>
            <Page size="A4" style={styles.page}>
                <Image style={styles.logo} src={logo} />
                <Text style={styles.title}>College Name - Department</Text>

                <Text style={styles.content}>
                    Identifier: {complaintData.identifier}
                </Text>
                <Text style={styles.content}>
                    Title: {complaintData.title}
                </Text>
                <Text style={styles.content}>
                    Issue: {complaintData.issue}
                </Text>
                <Text style={styles.content}>
                    Location: {complaintData.location}
                </Text>
                <Text style={styles.content}>
                    Nature: {complaintData.nature}
                </Text>
                <Text style={styles.content}>
                    Phone: {complaintData.phone}
                </Text>
                <Text style={styles.content}>
                    Priority: {complaintData.priority}
                </Text>
                {/* Include more fields as necessary */}

                {/* If there is an image, show 'Image Attached' text */}
                {complaintData.image && (
                    <Text style={styles.content}>
                        Image Attached: {complaintData.image.name}
                    </Text>
                )}

                <Image style={styles.seal} src={seal} />
                <Text style={styles.footer}>Department Seal</Text>

                <Image style={styles.signature} src={signaturePlaceholder} />
                <Text style={styles.footer}>Estate Office Sign</Text>
            </Page>
        </Document>
    );

    return (
        <div className="complaint-form-container" onClick={(e) => e.stopPropagation()}>
            {!showPreview ? (
                <form onSubmit={handleSubmit} encType="multipart/form-data">
                    <div className="input-group">
                        <label htmlFor="identifier">Identifier</label>
                        <input
                            type="text"
                            id="identifier"
                            name="identifier"
                            value={complaintData.identifier}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="title">Title</label>
                        <input
                            type="text"
                            id="title"
                            name="title"
                            value={complaintData.title}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="issue">Issue</label>
                        <textarea
                            id="issue"
                            name="issue"
                            value={complaintData.issue}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="location">Location</label>
                        <input
                            type="text"
                            id="location"
                            name="location"
                            value={complaintData.location}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="nature">Nature of the Complaint</label>
                        <input
                            type="text"
                            id="nature"
                            name="nature"
                            value={complaintData.nature}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="phone">Phone Number</label>
                        <input
                            type="tel"
                            id="phone"
                            name="phone"
                            value={complaintData.phone}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="priority">Priority</label>
                        <select
                            id="priority"
                            name="priority"
                            value={complaintData.priority}
                            onChange={handleChange}
                            required
                        >
                            <option value="low">Low</option>
                            <option value="medium">Medium</option>
                            <option value="high">High</option>
                        </select>
                    </div>
                    <div className="input-group">
                        <label htmlFor="image">Image (Optional)</label>
                        <input
                            type="file"
                            id="image"
                            name="image"
                            onChange={handleImageChange}
                            accept="image/*"
                        />
                    </div>
                    <button type="submit">Submit Complaint</button>
                </form>
            ) : (
                <BlobProvider document={ComplaintDocument}>
                    {({ blob, url, loading, error }) => {
                        if (loading) {
                            return <div>Loading preview...</div>;
                        }
                        return (
                            <div>
                                <iframe src={url} style={{ width: '100%', height: '500px' }} title="Preview" />
                                <div>
                                    <button onClick={handleEdit}>Edit</button>
                                    <button onClick={handleFinalSubmit}>Confirm and Submit</button>
                                    <a href={url} download="ComplaintDetails.pdf">
                                        <button>Download PDF</button>
                                    </a>
                                </div>
                            </div>
                        );
                    }}
                </BlobProvider>
            )}
        </div>
    );
}

export default ComplaintForm;
