import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { PDFDownloadLink, Document, Page, Text, View, StyleSheet } from '@react-pdf/renderer';
import '../css/ComplaintsForm.css';

// Styles for the PDF document
const styles = StyleSheet.create({
  page: {
    flexDirection: 'column',
    backgroundColor: '#fff',
    padding: 10
  },
  section: {
    margin: 10,
    padding: 10,
    flexGrow: 1
  },
  title: {
    fontSize: 18,
    marginBottom: 20,
  }
});

function ComplaintForm() {
    const { userType } = useParams();
    const navigate = useNavigate();
    const [complaintData, setComplaintData] = useState({
        identifier: '',
        title: '',
        issue: '',
        location: '',
        nature: '',
        phone: '',
        priority: '',
        image: null
    });
    const [submitted, setSubmitted] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setComplaintData({ ...complaintData, [name]: value });
    };

    const handleImageChange = (e) => {
        setComplaintData({ ...complaintData, image: e.target.files[0] });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setSubmitted(true);
    };

    const ComplaintDocument = () => (
        <Document>
            <Page size="A4" style={styles.page}>
                <View style={styles.section}>
                    <Text style={styles.title}>Complaint Details</Text>
                    <Text>Identifier: {complaintData.identifier}</Text>
                    <Text>Title: {complaintData.title}</Text>
                    <Text>Issue: {complaintData.issue}</Text>
                    <Text>Location: {complaintData.location}</Text>
                    <Text>Nature: {complaintData.nature}</Text>
                    <Text>Phone: {complaintData.phone}</Text>
                    <Text>Priority: {complaintData.priority}</Text>
                    {complaintData.image && (
                        <Text>Image Attached: {complaintData.image.name}</Text>
                    )}
                </View>
            </Page>
        </Document>
    );

    return (
        <div className="complaint-form-container" onClick={(e) => e.stopPropagation()}>
            {!submitted ? (
                <form onSubmit={handleSubmit} encType="multipart/form-data">
                    <div className="input-group">
                        <label htmlFor="identifier">Identifier</label>
                        <input
                            type="text"
                            id="identifier"
                            name="identifier"
                            value={complaintData.identifier}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="title">Title</label>
                        <input
                            type="text"
                            id="title"
                            name="title"
                            value={complaintData.title}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="issue">Issue</label>
                        <textarea
                            id="issue"
                            name="issue"
                            value={complaintData.issue}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="location">Location</label>
                        <input
                            type="text"
                            id="location"
                            name="location"
                            value={complaintData.location}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="nature">Nature of the Complaint</label>
                        <input
                            type="text"
                            id="nature"
                            name="nature"
                            value={complaintData.nature}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="phone">Phone Number</label>
                        <input
                            type="tel"
                            id="phone"
                            name="phone"
                            value={complaintData.phone}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="priority">Priority</label>
                        <select
                            id="priority"
                            name="priority"
                            value={complaintData.priority}
                            onChange={handleChange}
                            required
                        >
                            <option value="low">Low</option>
                            <option value="medium">Medium</option>
                            <option value="high">High</option>
                        </select>
                    </div>
                    <div className="input-group">
                        <label htmlFor="image">Image (Optional)</label>
                        <input
                            type="file"
                            id="image"
                            name="image"
                            onChange={handleImageChange}
                            accept="image/*"
                        />
                    </div>
                    <button type="submit">Submit Complaint</button>
                </form>
            ) : (
                <PDFDownloadLink document={<ComplaintDocument />} fileName="ComplaintDetails.pdf">
                    {({ blob, url, loading, error }) =>
                        loading ? 'Loading document...' : 'Download now!'
                    }
                </PDFDownloadLink>
            )}
        </div>
    );
}

export default ComplaintForm;
