import React, { useState } from 'react';
import '../css/ComplaintsTable.css';

const ComplaintsTable = ({ complaints, markAsDone }) => {
    const [sortedComplaints, setSortedComplaints] = useState(complaints);

    const sortComplaintsByMonth = () => {
        const sorted = [...complaints].sort((a, b) => {
            const dateA = new Date(a.date);
            const dateB = new Date(b.date);
            return dateA - dateB;
        });
        setSortedComplaints(sorted);
    };

    return (
        <div>
            <div className="table-header">
                <button onClick={sortComplaintsByMonth}>Sort by Month</button>
            </div>
            <table className="complaints-table">
                <thead>
                    <tr>
                        <th>Identifier</th>
                        <th>Title</th>
                        <th>Issue</th>
                        <th>Location</th>
                        <th>Assigned Worker</th>
                        <th>Status</th>
                        <th>Date</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {sortedComplaints.map((complaint, index) => (
                        <tr key={index}>
                            <td>{complaint.identifier}</td>
                            <td>{complaint.title}</td>
                            <td>{complaint.issue}</td>
                            <td>{complaint.location}</td>
                            <td>{complaint.assignedWorker}</td>
                            <td>{complaint.status}</td>
                            <td>{new Date(complaint.date).toLocaleDateString()}</td>
                            <td>
                                {complaint.status === 'In Progress' && (
                                    <button onClick={() => markAsDone(index)}>Mark as Done</button>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ComplaintsTable;
